import React from 'react'
import Slider from './Components/Slider'
import Image1 from '/Users/mac/Desktop/Darasimi files/Online-Mini-Store-REST-API-x-Reactjs-/frontend/src/assets/images/image.jpg';
import Image2 from '/Users/mac/Desktop/Darasimi files/Online-Mini-Store-REST-API-x-Reactjs-/frontend/src/assets/images/image2.jpg';
import Image3 from '/Users/mac/Desktop/Darasimi files/Online-Mini-Store-REST-API-x-Reactjs-/frontend/src/assets/images/image3.jpg';

const images = [Image1, Image2, Image3];
function App() {
  return (
    <div>
    <div style={{ display: 'flex', justifyContent: 'space-between' }} />
    <Slider
    style={{ backgroundColor: 'black'}}
      options={{
        pauseAutoPlayOnHover: true,
        fullscreen: true,
        pageDots: true,
        adaptiveHeight: true,
      }}
    >
      {images.map((image, index) => (
        <div style={{ backgroundPosition: 'center center', backgroundSize: 'cover', width: '66%', height: '500px', margin: '50px 5px' }} key={index}>
          <img style={{ width: '100%'}} src={image} alt="" />
        </div>
      ))}
    </Slider>
  </div>
  )
}

export default App
