import React, { useContext, useState, useRef, useEffect } from "react"
import { useParams } from "react-router-dom"
// import Lists from "./List"
import axios from "axios"
// import Lists from "./List3"
import styles from './product.module.css'
import CartContext from "./Store/cart-context"

function Product(props) {
  let [amount, setAmount] = useState('1')
  let [products, setProducts] = useState([])
  let [added, setAdded] = useState(false)
  let [inputIsValid, setInputIsValid] = useState(false)
  let [imgIndex, setImgIndex] = useState(0)
  let amountInputRef = useRef()
  let ctx = useContext(CartContext)

  const { productId } = useParams()
  // console.log(productId)
  const url = `https://thegorana.herokuapp.com/products/${productId}`
  const getProduct = async () => {
    // setLoading(true)
    axios.get(url).then((response) => {
      let data = response.data
      setProducts(data)
      // setLoading(false)
    })
  }
  // getProduct();
  console.log(products);
  useEffect(() => {
    getProduct();
  }, [url]);

  // const products = props.Lists.find(prod => String(prod._id) === productId)
  // const products = productPage.find(prod => String(prod._id) === productId)
  // console.log(products)


  const addToCartHandler = (amount) => {
    ctx.addItem({
      key: products._id,
      id: products._id,
      // key: productId.id,
      // id: productId.id,
      title: products.name,
      images: products.images[0],
      amount: amount,
      price: products.price,
    });
  }

  let amountFormHandler = (e) => {
    e.preventDefault()
    let enteredAmount = amountInputRef.current.value
    let enteredAmountNum = +enteredAmount

    if (enteredAmount.trim().length === 0 ||
      enteredAmountNum < 1 ||
      enteredAmountNum > 9) {
      setInputIsValid(true);
      setAdded(false)
      setTimeout(() => {
        setInputIsValid(false)
      }, 2000)
      return;
    } else {
      setInputIsValid(false);
    }

    addToCartHandler(enteredAmountNum)
    setAmount('1')
  }

  let amountChangeHandler = (e) => {
    setAmount(e.target.value)
  }
  let addedToCartHandler = () => {
    setAdded(true)
    setTimeout(() => {
      setAdded(false)
    }, 2000)
  }

  return (
    <div className={styles.body1}>
      {products.map((productss) => {
        <div className={styles.body}>
          <div className={styles.images}>
            <div className={styles.imagesInner}>
              <div className={styles.wrapper}>
                <img src={productss.images[imgIndex]} alt={productss.title}></img>
              </div>
              <div className={styles.imageSmall}>
                <div className={styles.wrapper2}>
                  <img src={productss.images[0]} alt={productss.title} onClick={() => setImgIndex(0)}></img>
                </div>
                <div className={styles.wrapper2}>
                  <img src={productss.images[1]} alt={productss.title} onClick={() => setImgIndex(1)}></img>
                </div>
                <div className={styles.wrapper2}>
                  <img src={productss.images[2]} alt={productss.title} onClick={() => setImgIndex(2)}></img>
                </div>
              </div>
            </div>
          </div>
          <div className={styles.descrip}>
            <h1>{productss.name}</h1>
            <p className={styles.price}>Price: ₦{productss.price}</p>
            <p>Description: {productss.description}</p>
            <div className={styles.list}>
              {inputIsValid && <p>Can't add more than 9 items at once.</p>}
              {added && <p>Item added to cart</p>}
              <form onSubmit={amountFormHandler}>
                <label>Quantity:</label>
                <input
                  ref={amountInputRef}
                  // id={Lists.id}
                  type='number'
                  min='1'
                  max='10'
                  step='1'
                  value={amount}
                  onChange={amountChangeHandler}
                />
                <button
                  onClick={addedToCartHandler}
                  className={styles.button}>Add to cart</button>
              </form>
            </div>
          </div>
        </div>
      })}

    </div>
  )
}

export default Product
