import React, { useState, useEffect } from 'react'
import NavBar from './NavBar'
import styles from './Contact.module.css'


function Contact() {
  let [firstName, setFirstName] = useState('')
  let [formIsValid, setFormIsValid] = useState(false)
  let [message, setMessage] = useState('')
  let [subject, setSubject] = useState('')
  let [email, setEmail] = useState('')


  useEffect(() => {
    if (firstName.trim().length >= 1 && message.trim().length >= 1 && message.trim().length >= 1 && email.includes('@') && email.includes('.com')) {
      setFormIsValid(true)
    } else {
      setFormIsValid(false)
    }
  }, [firstName, message, subject, email])
  let formSubmitHandler = (e) => {
    // e.preventDefault()
  }

  let FirstNameChangeHandler = (e) => {
    setFirstName(e.target.value)
  }

  let EmailChangeHandler = (e) => {
    setEmail(e.target.value)
  }
  let subjectChangeHandler = (e) => {
    setSubject(e.target.value)
  }
  let messageChangeHandler = (e) => {
    setMessage(e.target.value)
  }
  return (
    <div>
      <NavBar />
      <div className={styles.body}>
        <div className={styles.body_child}>
          <h1>CONTACT</h1>
        </div>
        <div className={styles.body_inner}>
          <div className={styles.form}>
            <form onSubmit={formSubmitHandler}>
              <div className={styles.checkoutBody_inner_name}>
                <div className={styles.checkoutBody_inner_name_inner}>
                  <label>Name</label>
                  <input
                    type='text'
                    value={firstName}
                    onChange={FirstNameChangeHandler}
                    placeholder='John'
                  />
                </div>
                <div className={styles.checkoutBody_inner_name_inner}>
                  <label>Email</label>
                  <input
                    type='email'
                    value={email}
                    onChange={EmailChangeHandler}
                    placeholder='Johndoe@email.com'
                  />
                </div>
              </div>
              <div>
                <label>Subject</label>
                <input
                  type='text'
                  value={subject}
                  onChange={subjectChangeHandler}
                />
              </div>
              <div>
                <label>Message*</label>
                <textarea
                  type='text'
                  value={message}
                  onChange={messageChangeHandler}
                />
              </div>
              <button
                type='submit'
                className={styles.button}
                disabled={!formIsValid}>
                Send message</button>
            </form>
          </div>
          <div className={styles.contactInfo}>
            <div className={styles.contactInfo_list}>

            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Contact
