import React, { useContext, useState, useRef, useEffect } from "react"
import { useParams } from "react-router-dom"
// import Lists from "./List"
// import axios from "axios"
// import Lists from "./List3"
import styles from './product.module.css'
import CartContext from "./Store/cart-context"

function Product(props) {
  let [amount, setAmount] = useState('1')
  const { productId } = useParams()
  let [productPage, setProductPage] = useState([])
  let [added, setAdded] = useState(false)
  let [inputIsValid, setInputIsValid] = useState(false)
  let [imgIndex, setImgIndex] = useState(0)
  let amountInputRef = useRef()
  let ctx = useContext(CartContext)

  useEffect(() => {
    let getProduct = async () => {
      let response = await fetch(`https://thegorana.herokuapp.com/products/${productId}`)
      let data = await response.json()
      let items = []
      let itemList = data
      for (let itemLists of itemList) {
        items.push({
          id: itemLists._id,
          name: itemLists.name,
          description: itemLists.description,
          price: itemLists.price,
          images: itemLists.images
        })
        setProductPage(items)
      }
    }
    getProduct()
  }, [productId])

  const products = productPage.find(prod => String(prod.id) === productId)


  const addToCartHandler = (amount) => {
    ctx.addItem({
      // key: products._id,
      // id: products._id,
      key: productId.id,
      id: productId.id,
      title: products.name,
      images: products.images[0],
      amount: amount,
      price: products.price,
    });
  }

  let amountFormHandler = (e) => {
    e.preventDefault()
    let enteredAmount = amountInputRef.current.value
    let enteredAmountNum = +enteredAmount

    if (enteredAmount.trim().length === 0 ||
      enteredAmountNum < 1 ||
      enteredAmountNum > 9) {
      setInputIsValid(true);
      setAdded(false)
      setTimeout(() => {
        setInputIsValid(false)
      }, 2000)
      return;
    } else {
      setInputIsValid(false);
    }

    addToCartHandler(enteredAmountNum)
    setAmount('1')
  }

  let amountChangeHandler = (e) => {
    setAmount(e.target.value)
  }
  let addedToCartHandler = () => {
    setAdded(true)
    setTimeout(() => {
      setAdded(false)
    }, 2000)
  }

  return (
    <div className={styles.body1}>
      {productPage.map((products) => (
        <div className={styles.body} >
          <div className={styles.images}>
            <div className={styles.imagesInner}>
              <div className={styles.wrapper}>
                <img src={products.images[imgIndex]} alt={products.title}></img>
              </div>
              <div className={styles.imageSmall}>
                <div className={styles.wrapper2}>
                  <img src={products.images[0]} alt={products.title} onClick={() => setImgIndex(0)}></img>
                </div>
                <div className={styles.wrapper2}>
                  <img src={products.images[1]} alt={products.title} onClick={() => setImgIndex(1)}></img>
                </div>
                <div className={styles.wrapper2}>
                  <img src={products.images[2]} alt={products.title} onClick={() => setImgIndex(2)}></img>
                </div>
              </div>
            </div>
          </div>
          <div className={styles.descrip}>
            <h1>{products.title}</h1>
            <p className={styles.price}>Price: ₦{products.price}</p>
            <p>Description: {products.description}</p>
            <div className={styles.list}>
              {inputIsValid && <p>Can't add more than 9 items at once.</p>}
              {added && <p>Item added to cart</p>}
              <form onSubmit={amountFormHandler}>
                <label>Quantity:</label>
                <input
                  ref={amountInputRef}
                  // id={Lists.id}
                  type='number'
                  min='1'
                  max='10'
                  step='1'
                  value={amount}
                  onChange={amountChangeHandler}
                />
                <button
                  onClick={addedToCartHandler}
                  className={styles.button}>Add to cart</button>
              </form>
            </div>
          </div>
        </div>
      ))}
    </div >
  )
}

export default Product
