import React, { useState, useEffect } from 'react'
import NavBar from './Components/NavBar';
import Lists from './Components/List';
import styles from "./App.module.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Main from './Components/Main';
import Product from './Components/Product';


function App() {
  let [search, setSearch] = useState('')
  let [empty, setEmpty] = useState(false)
  const [filteredResults, setFilteredResults] = useState(Lists);

  useEffect(() => {
    const filterResults = Lists.filter((item) =>
      item.title.toLowerCase().includes(search.toLowerCase())
    );
    setFilteredResults(filterResults);
    if (filterResults.length === 0) {
      setEmpty(true)
    } else {
      setEmpty(false)
    }
  }, [search]);
  return (
    <div>
       <div>
        <NavBar search={search} setSearch={setSearch} empty={empty} />
      </div>
      <div>
        <div className={styles.nav}>
          <Routes>
            <Route path="/" element={<Main Lists={filteredResults} empty={empty} search={search} />} />
            <Route path="/product" element={<Product />} />
          </Routes>
        </div>
      </div>
    </div>
  )
}

export default App
